class Solution(object):
	def numberOfBoomerangs(self, points):
		"""
		:type points: List[List[int]]
		:rtype: int
		"""
		# print len(points)
		from collections import defaultdict
		points = map(tuple, points)
		ret = 0
		for i in points:
			same_dis = defaultdict(int)
			for j in points:
				dis = (i[0]-j[0])**2 + (i[1]-j[1])**2
				same_dis[dis] += 1
			for j in same_dis:
				ret += same_dis[j]*(same_dis[j]-1)

		return ret 


